package app.demo.domain.LoanDetail;import app.demo.domain.rest.Meta;import java.io.Serializable;import java.util.List;public class ResponseLoanDetail implements Serializable {    public Meta meta;    public LoanDetail data;    public List<Error> errors;    public ResponseLoanDetail() {    }    public ResponseLoanDetail(Meta meta, LoanDetail data) {        this.meta = meta;        this.data = data;    }    public Meta getMeta() {        return meta;    }    public void setMeta(Meta meta) {        this.meta = meta;    }    public LoanDetail getData() {        return data;    }    public void setData(LoanDetail data) {        this.data = data;    }}